import PropTypes from 'prop-types';
import React from 'react';

import formatDate from '../helpers';

function formatShow(show) {
  return show.replace(/- Results/, '');
}

function getDiff(currentClass, i) {
  const raw = parseFloat(currentClass.scores[i - 1].score) -
    parseFloat(currentClass.scores[i].score);
  return Math.round(raw * 100) / 100;
}

function Classes({ classes }) {
  return (
    <div className="w-100">
      {Object.keys(classes).map(c => (
                classes[c].scores ? (
                  <div key={c}>
                    <div
                      className="pt-5 score"
                      id={classes[c].shortName ? classes[c].shortName.toLowerCase() : null}
                    >
                      <h4 className="font-weight-bold mb-4 px-3">{c}</h4>
                    </div>

                    {classes[c].scores.length ? 

                    <table className="table table-striped">
                      <thead className="border-bottom-0">
                        <tr className="d-flex">
                          <th className="col-2 col-sm-1 d-inline-block border-bottom-0">Rank</th>
                          <th className="col-6 col-sm-4 d-inline-block border-bottom-0">Ensemble</th>
                          <th className="col-4 col-sm-2 d-inline-block border-bottom-0">Score</th>
                          <th className="col-2 d-sm-inline-block d-none border-bottom-0">Date</th>
                          <th className="col-3 d-sm-inline-block d-none border-bottom-0">Regional</th>
                        </tr>
                      </thead>
                      <tbody>
                        {classes[c].scores.map((score, i) => (
                          <tr key={score.name} className="results-row d-flex">
                            <td className="col-2 col-sm-1">{i + 1}</td>
                            <td className="col-6 col-sm-4">
                              {score.url ? (
                                <a href={score.url} target="_blank" rel="noopener noreferrer">{score.name}</a>
                                            ) : (
                                              <div>
                                                {score.name}
                                              </div>
                                            )}
                            </td>
                            <td className="col-4 col-sm-2">
                              {score.score}
                              {i ? (
                                <span className="diff">
                      (-{getDiff(classes[c], i)})
                                </span>
                                            ) : null}
                            </td>
                            <td className="col-2 d-none d-sm-block">
                              {formatDate(score.date)}
                            </td>
                            <td className="col-3 d-none d-sm-block">
                              {score.showUrl ? (
                                <a href={score.showUrl} target="_blank" rel="noopener noreferrer">
                                  {formatShow(score.show)}
                                </a>
                                            ) : (
                                              <div>
                                                {formatShow(score.show)}
                                              </div>
                                            )}
                            </td>
                          </tr>
                                ))}
                      </tbody>
                    </table> :  <div className="mb-4 px-3">No scores in this class</div>}
                  </div>
                ) : null
            ))}
    </div>
  );
}

Classes.propTypes = {
  classes: PropTypes.object.isRequired, // eslint-disable-line react/forbid-prop-types
};

export default Classes;
