import React from 'react';

function goToTop() {
  window.scrollTo(0, 0);
  document.body.className = '';
}

class ScrollToTop extends React.Component {
  constructor(props) {
    super(props);
    this.state = { showGoToTop: false };
  }
  componentDidMount() {
    const nav = document.getElementById('nav');
    window.addEventListener('scroll', () => {
      const navTop = nav.getBoundingClientRect().top;

      if (navTop <= 0 && this.state.showGoToTop === false) {
        this.setState({ showGoToTop: true });
        document.body.className = 'scrolled';
      } else if (window.scrollY === 0 && this.state.showGoToTop === true) {
        this.setState({ showGoToTop: false });
        document.body.className = '';
      }
    });
  }

  render() {
    const { showGoToTop } = this.state;
    return (
      <button
        className={`scroll-to-top${showGoToTop ? ' visible' : ''}`}
        onClick={goToTop}
      >
        Go to top
      </button>
    );
  }
}

export default ScrollToTop;
