export const Home = () => {
    const data = {
            "work": [
          {
            "name": "Current",
            "items": [
              {
                "title": "Shocase, Inc.",
                "description": "Front End Engineer",
                "time": "2013-Present",
                "numYears": 2,
                "content": "Building front-end user experiences for a brand new social network for marketers."
              }
            ]
          },
          {
            "name": "Past",
            "items": [
              {
                "title": "Autodesk, Inc.",
                "description": "Front End Engineer",
                "time": "2012-2013",
                "numYears": 1,
                "content": "Building front-end user experiences in HTML5/ CSS3/ JavaScript/ jQuery."
              },
              {
                "title": "Autodesk, Inc.",
                "description": "Senior Interaction Designer",
                "time": "2007-2012",
                "numYears": 5,
                "content": "Designing user experiences for AutoCAD software."
              },
              {
                "title": "Autodesk, Inc.",
                "description": "QA Engineer/Lead",
                "time": "2004-2007",
                "numYears": 3,
                "content": "Black Box QA Engineer for AutoCAD software."
              }
            ]
          },
          {
            "name": "Education",
            "items": [
              {
                "title": "University of California, Los Angeles",
                "description": "M.S., Mechanical Engineering",
                "time": "2003-2004",
                "numYears": 1,
                "content": "Focus on controls and dynamics of mechanical systems."
              },
              {
                "title": "University of California, Berkeley",
                "description": "B.S., Mechanical Engineering",
                "time": "1998-2003",
                "numYears": 5,
                "content": "Activities: Cal Band"
              }
            ]
          }
        ],
        "play": [
          {
            "title": "Playing/Teaching Percussion",
            "description": [
              "I'm currently focusing on playing drumset, though not playing with any groups (yet). I've also taught and arranged for marching/rudimental percussion at the High School, University, Drum Corps (DCI) and WGI levels.<br/><br/>Here are some of the groups I have taught and their accomplishments:<br/><br/>"
            ],
            "teachingGroups": [
              {
                "href": "http://www.beyerband.org/",
                "title": "Beyer High School",
                "location": "Modesto, CA",
                "subtitle": [
                  {
                    "title": "2004-2005 - Battery Tech",
                    "accolades": [
                      "2005 - WBA AAA Champion"
                    ]
                  },
                  {
                    "title": "2006-2007 - Caption Head/Fall Battery Arranger",
                    "accolades": [
                      "2006 - WBA AAA Champion",
                      "2007 - WBA AAA Champion",
                      "2007 - WGI Scholastic Open Class Bronze Medalist"
                    ]
                  },
                  {
                    "title": "2008-2009 - Fall Battery Arranger",
                    "accolades": [
                      "2008 - WBA AAA Champion"
                    ]
                  },
                  {
                    "title": "2010-2012 - Fall/Winter Battery Arranger"
                  }
                ]
              },
              {
                "href": "https://www.scvanguard.org/programs/vanguard_cadets/",
                "title": "Santa Clara Vanguard Cadets",
                "location": "Santa Clara, CA",
                "subtitle": [
                  {
                    "title": "2006, 2008, 2010 - Guest Tech",
                    "accolades": [
                      "2008 - DCI Open Class Champions, High Drums"
                    ]
                  }
                ]
              },
              {
                "href": "https://www.scvanguard.org/programs/winter_percussion/",
                "title": "Vanguard Winter Percussion",
                "location": "Santa Clara, CA",
                "subtitle": [
                  {
                    "title": "2010-2011, 2014 - Battery Tech/Consultant",
                    "accolades": [
                      "2010 - WGI Independent Open Class Champions"
                    ]
                  },
                  {
                    "title": "2012-2013 - Battery Arranger",
                    "accolades": [
                      "2011 - Promoted from WGI Independent Open to World Class"
                    ]
                  }
                ]
              }
            ]
          },
          {
            "title": "Homebrewing Beer",
            "description": [
              "I'm a member of",
              {
                "href": "http://sfhomebrewersguild.com/about/",
                "text": "SFHG"
              },
              ", and love to try new recipes and build some of",
              {
                "href": "https://www.brewtoad.com/users/10932/recipes?page=1&sort=rank&scope_to_user_id=10932",
                "text": "my own."
              },
              "Really though, I love sharing something I've proudly created with friends."
            ]
          },
          {
            "title": "2 Wheels!",
            "description": [
              "I love",
              {
                "href": "http://www.strava.com/athletes/12286",
                "text": "mountain & road biking, as well as commuting by bike"
              },
              ". ",
              "I also have a 2004 Suzuki GS500F (naked conversion) that gets some action commuting and the occasional weekend ride. All in all, I'm more comfortable on 2 wheels than anything else (skates, skateboards, skis etc.)."
            ]
          }
        ]
      };
    return (
<>
        <div className="sk-font-loader">
    <div className="sk-font-loader-1"></div>
    <div className="sk-font-loader-2"></div>
    <div className="sk-font-loader-3"></div>
</div>    
<div className="sk-content-wrap">
    <div id="sk-intro">
        <div className="sk-intro-content-wrap sk-font-dependency">
            <h1 className="sk-intro-header">
                <p>Hi there!</p>

                <p>I'm Shekhar Khedekar.</p>
            </h1>
            <hr className="sk-hr"/>

            <p className="sk-intro-content">
                I'm a front-end engineer at <a href="http://corp.shocase.com/about-us/" target="_blank"
                                               className="sk-link-dark">Shocase</a> and
                a
                percussionist, cyclist, and home-brewer.
            </p>

            <p className="sk-intro-content">
                If you'd like to get in touch,
                <a href="mailto:shekhar.khedekar+website@gmail.com?subject=Contacting you from shekharkhedekar.com"
                   title="email" className="sk-link-dark">email me,</a>
                connect on <a href="http://www.linkedin.com/in/shekharkhedekar" title="linkedIn"
                              target="_blank" className="sk-link-dark">LinkedIn,</a>
                or find me on <a href="http://www.facebook.com/shekhar.khedekar" title="facebook"
                                 target="_blank" className="sk-link-dark">Facebook,</a>
                <a href="https://twitter.com/shekhar" title="twitter" target="_blank" className="sk-link-dark">Twitter,</a>
                and
                <a href="http://instagram.com/shekhark" title="instagram" target="_blank"
                   className="sk-link-dark">Instagram</a>.
            </p>
        </div>
        <div className="sk-intro-down-arrow sk-arrow-down2"></div>
    </div>

    <div id="sk-work">
        <div className="sk-work-wrap sk-font-dependency">
            <h2 className="sk-main-header">Work</h2>
            <a href="Shekhar Khedekar.pdf" target="_blank" className="sk-work-resume sk-in">Download Resume</a>

            <div className="sk-work-categories">
                {data.work.map(w => (
                    <div className="sk-work-category">
                        <h3 className="sk-work-header">{w.name}</h3>
                        <hr className="sk-hr-dark sk-work-hr"/>
                        {item.items.map(i => (
                            <div className="sk-work-item">
                                <div className="sk-work-item-title">{i.title}</div>
                                <div className="sk-work-item-description">{i.description}</div>
                                <div className="sk-work-item-time">{i.time}</div>
