import PropTypes from 'prop-types';
import React from 'react';

import formatDate from '../helpers';

function formatShow(show) {
  return show.replace(/- Results/, '');
}

function getDiff(currentClass, i) {
  if (!i) {
    return 0;
  }

  const raw = parseFloat(currentClass.scores[i - 1].score) -
    parseFloat(currentClass.scores[i].score);
  return Math.round(raw * 100) / 100;
}

function VerticalClasses({ classes }) {
  return (
    <div className="px-3 container-fluid">
      {Object.keys(classes).map(c => (
                classes[c].scores ? (
                  <div className="row d-block" key={c}>

                    <div
                      className="pt-5 px-3 score border-top border-bottom"
                      id={classes[c].shortName ? classes[c].shortName.toLowerCase() : null}
                    >
                      <h4 className="font-weight-bold mb-4">{c}</h4>
                    </div>

                    {classes[c].scores.length ? <ul className="scores px-3">
                      {classes[c].scores.map((score, i) => (
                        <li
                          className="score"
                          style={{
                                        marginTop: `${(Math.min(getDiff(classes[c], i), 8) * 1.25) + 1}rem`,
                                    }}
                          key={score.name}
                        >
                          <div className="score-rank">{i + 1}</div>
                          <div className="score-info">
                            <span className="score-unit">
                              {score.url ? (
                                <a href={score.url} target="_blank" rel="noopener noreferrer">{score.name}</a>
                                            ) : (
                                              <span>
                                                {score.name}
                                              </span>
                                            )} -
                            </span>
                            <span className="score-score">
                              {score.score}
                            </span>
                            <span className="d-none d-sm-inline">
                      - {score.showUrl ? (
                        <a href={score.showUrl} target="_blank" rel="noopener noreferrer">
                          {formatShow(score.show)}
                        </a>
                                            ) : (
                                              <span>
                                                {formatShow(score.show)}
                                              </span>
                                            )}

                              <span className="score-date">({formatDate(score.date)})</span>
                            </span>
                          </div>
                        </li>
                            ))}
                    </ul> :  <div className="mt-4 mb-4 px-3">No scores in this class</div>}
                  </div>
                ) : null
            ))}
    </div>
  );
}

VerticalClasses.propTypes = {
  classes: PropTypes.object.isRequired, // eslint-disable-line react/forbid-prop-types
};

export default VerticalClasses;
